                        charsAtPoints.Add(arr3D[j + 1, k, i - 1]); // (1, 0, -1)   
                        charsAtPoints.Add(arr3D[j + 1, k + 1, i - 1]); // (1, 1, -1)

                        var shouldChange = ShouldChange(IsActive(arr3D[j, k, i]), charsAtPoints);

                        if (shouldChange)
                            if (currentChar.Equals(active)) currentChar = inactive;
                            else if (currentChar.Equals(inactive)) currentChar = active;
                            else Console.WriteLine("Something went wrong.");

                        newArray[j, k, i] = currentChar;
                    }
                }
            }

            return newArray;
        }

        private static bool ShouldChange(bool active, List<char> closestChars)
        {
            var count = 0;
            foreach (var cat in closestChars)
                if (IsActive(cat)) count++;

            // if a cube is active and exactly 2 or 3 of its neighbors are also active,
            // the cube remains active.
            if (active)
                if (count == 2 || count == 3)
                    return false;
                else return true;

            // if a cube is inactive but exactly three of its neighbors are active,
            // the cube becomes active.
            if (!active && count == 3) return true;
            else return false;
        }

        private static bool IsActive(char a)
        {
            return a.Equals(active);
        }
    }
}
